import React, { useEffect } from "react";
import { useState } from "react";
import { useParams } from "react-router-dom";
import Loader from '../components/Loader'
import { useNavigate } from "react-router-dom";


function MovieDetailPage() {

    const nav = useNavigate()
    const {id} = useParams()
    const [movieDetails, setMovieDetails] = useState([''])
    const [video, setVideo] = useState([''])
    const [videoKey, setVideoKey] = useState([''])
    
    useEffect(()=> {
        const url = `https://api.themoviedb.org/3/movie/${id}?api_key=6cdab13971e086e7f76a7fd28fe6a1ad&query=&language=en-US`
        fetch(url)
        .then(response => response.json())
        .then(data => {
            setMovieDetails(data)
            console.log(data)
        })
        .catch(err => {
            console.log(err)
            nav('/404')
        })
    }, [id]) 
    useEffect(()=> {
        const urlVideo = `https://api.themoviedb.org/3/movie/${id}?&append_to_response=videos&api_key=6cdab13971e086e7f76a7fd28fe6a1ad`
        fetch(urlVideo)
        .then(response => response.json())
        .then(data => {
            setVideo(data.videos.results);
            console.log(data.videos.results)
            video.map((vidKey) => {
                if(vidKey.name === 'Official Trailer' || vidKey.name === 'Official Teaser Trailer') {
                    setVideoKey(vidKey.key)
                    console.log(vidKey.key)
                }
            })
        })
        .catch(err => {
            console.error(err)
            nav('/404')
        })
        
    }, [video])
    // const results = <MovieCard movieDetails = {movieDetails}/>
    // const backdropPath = `https://image.tmdb.org/t/p/original${movieDetails.backdrop_path}`
    return ( 
        <div>
            {movieDetails ?
                (
                    <>
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-2">
                                    side nav
                                </div>
                                <div className="col-10 p-5">
                                    <div className="row">
                                        <div className="col-12">
                                            {videoKey ?
                                            (    <iframe
                                                    className="rounded-5"
                                                    title="movie trailer"
                                                    id="videoPlayer"
                                                    width="100%"
                                                    height="400"
                                                    src={`https://www.youtube.com/embed/${videoKey}`}
                                                    framBorder="0"
                                                    allowFullScreen
                                                ></iframe>
                                            ) : <Loader />
                                            }
                                        </div>
                                        <div className="col-8">
                                            <p>{movieDetails.original_title}</p>
                                        </div>
                                        <div className="col-4 text-right">
                                            hello
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </> 
                ) : <Loader />
            }
        </div>
     );
}

export default MovieDetailPage;
