import React, { useEffect } from "react";
import { useState } from "react";
import { useParams } from "react-router-dom";
import Loader from '../components/Loader'
import { useNavigate } from "react-router-dom";


function MovieDetailPage() {

    const nav = useNavigate()
    const {id} = useParams()
    const [movieDetails, setMovieDetails] = useState([''])
    
    useEffect(()=> {
        const url = `https://api.themoviedb.org/3/movie/${id}?api_key=6cdab13971e086e7f76a7fd28fe6a1ad&query=&language=en-US`
        fetch(url)
        .then(response => response.json())
        .then(data => {
            setMovieDetails(data)
            console.log(data)
        })
        .catch(err => {
            console.log(err)
            nav('/404')
        })
    }, [id]) 
        // const results = <MovieCard movieDetails = {movieDetails}/>
         const backdropPath = `https://image.tmdb.org/t/p/original${movieDetails.backdrop_path}`
    return ( 
        <div>
            {movieDetails ?
                (
                    <>
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-4">
                                    side nav
                                </div>
                                <div className="col-8 ">
                                    <div className="row">
                                        <div className="col-12">
                                            <div className="movieD_img">
                                                <img className="overlayed-image" src={backdropPath} alt={movieDetails.original_title}  />
                                                {/* <div className="overlay"></div> */}
                                            </div>
                                        </div>
                                        <div className="col-8">
                                            <p>{movieDetails.original_title}</p>
                                        </div>
                                        <div className="col-4 text-right">
                                            hello
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </> 
                ) : <Loader />
            }
        </div>
     );
}

export default MovieDetailPage;
