import './App.css';
import { useState } from "react";
import { Routes, Route } from 'react-router-dom';
import HomePage from './pages/HomePage';
import MovieDetailPage from './pages/MovieDetailPage'
import SearchedMoviePage from './pages/SearchedMoviePage'
import Header from './layout/Header';
import Footer from './layout/Footer'
import { useLocation } from 'react-router-dom';

function App() {

  const [searchText, setSearchText] = useState('')
  const locate = useLocation()
  return (
    <div className="App">
      {locate.pathname == `/search/${searchText.replace()}` && 
      <Header searchText={searchText} setSearchText={setSearchText} background = {{background: '#080e1d'}}/>}
      <Header searchText={searchText} setSearchText={setSearchText}/>
      <Routes>
        <Route path='/' element={<HomePage />} />
        <Route path='/movie/:id' element={<MovieDetailPage />} />
        <Route path='/search/:param' element={<SearchedMoviePage searchText={searchText} />} />
      </Routes>
    </div>
  );
}

export default App;
