import React from "react";
import imageLoad from "../assests/imageLoad.jpg"
import Loader from "../pages/Loader";

function Card({movie, loading}) {
    const imgSrc = `https://image.tmdb.org/t/p/original${movie.poster_path}`
    return ( 
        <div >
            {loading ? <Loader />
            : movie.poster_path ? <img src={imgSrc} alt={movie.original_title} width={'100%'} />
            : <img src={imageLoad} alt={movie.original_title} width={'100%'} />
            }
            <p>{movie.original_title}</p>
        </div>
     );
}

export default Card;
