import React, { useEffect } from "react";
import { useState } from "react";
import { Link, useParams } from "react-router-dom";
import Loader from '../components/Loader'
import { useNavigate } from "react-router-dom";


function MovieDetailPage() {

    const nav = useNavigate();
    const { id } = useParams();
    const [movieDetails, setMovieDetails] = useState(null); // Initialize as null
    const [video, setVideo] = useState([]); // Initialize as an empty array
    const [videoKey, setVideoKey] = useState(''); // Initialize as an empty string

    useEffect(() => {
        const url = `https://api.themoviedb.org/3/movie/${id}?api_key=6cdab13971e086e7f76a7fd28fe6a1ad&query=&language=en-US`;

        fetch(url)
            .then(response => response.json())
            .then(data => {
                setMovieDetails(data);
            })
            .catch(err => {
                console.error(err);
                nav('/404');
            });
    }, [id]);

    useEffect(() => {
        const urlVideo = `https://api.themoviedb.org/3/movie/${id}?&append_to_response=videos&api_key=6cdab13971e086e7f76a7fd28fe6a1ad`;

        fetch(urlVideo)
            .then(response => response.json())
            .then(data => {
                const officialVideos = data.videos.results.filter(vidKey => (
                    vidKey.name === 'Official Trailer' || vidKey.name === 'Official Teaser Trailer'
                ));

                if (officialVideos.length > 0) {
                    setVideo(officialVideos);
                    setVideoKey(officialVideos[0].key);
                }
            })
            .catch(err => {
                console.error(err);
                nav('/404');
            });
    }, [id]); // Use [id] as the dependency, not [video]

    // const results = <MovieCard movieDetails = {movieDetails}/>
    // const backdropPath = `https://image.tmdb.org/t/p/original${movieDetails.backdrop_path}`
    return ( 
        <div>
            {movieDetails ?
                (
                    <>
                        <div className="container">
                            <div className="row">
                                <div className="col-lg-3">
                                    <div className="side-nav">
                                            <Link className='side-home' to='/'>MovieBox</Link>
                                            <Link to='/'>Home</Link>
                                            <Link to='/'>Movies</Link>
                                            <Link to='/'>TV Series</Link>
                                            <Link to='/'>Upcoming</Link>
                                        <div className="side-content">
                                            <h6>Lorem ipsum dolor elit. consectetur adipisicing elit. Lorem ipsum dolor sit amet consectetur  </h6>

                                            <p className="light-grey">Lorem ipsum, dolor sit amet </p>
                                            <button>click me</button>
                                        </div>
                                        <Link to='/'>Log out</Link>
                                    </div>
                                </div>
                                <div className="col-lg-9 mt-4">
                                    <div className="row">
                                        <div className="col-12">
                                            {movieDetails && videoKey ?
                                            (    <iframe
                                                    className="rounded-2"
                                                    title="movie trailer"
                                                    id="videoPlayer"
                                                    width="100%"
                                                    height="400"
                                                    src={`https://www.youtube.com/embed/${videoKey}`}
                                                    frameBorder="0"
                                                    allowFullScreen
                                                ></iframe>
                                            ) : <Loader />
                                            }
                                        </div>
                                        <div className="col-8">
                                            <p>{movieDetails.original_title}</p>
                                        </div>
                                        <div className="col-4 text-right">
                                            hello
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </> 
                ) : <Loader />
            }
        </div>
     );
}

export default MovieDetailPage;
