import React from "react";
import imageLoad from "../assests/imageLoad.jpg"
import Loader from "../pages/Loader";
import { Link } from "react-router-dom";

function Card({movie, loading}) {
    const movieUrl = `/movie/${(movie.id)}`
    const imgSrc = `https://image.tmdb.org/t/p/original${movie.poster_path}`
    return ( 
        <div className="card" data-testid="movie-card" >
            <Link to={movieUrl}>
                {
                loading ? <Loader /> 
                : 
                movie.poster_path == null ? 
                    <img data-testid="movie-po" src={imageLoad} alt={movie.original_title} width={'220px'} /> 
                : 
                <img data-testid="movie-po" src={imgSrc} alt={movie.original_title} width={'220px'} />
                }
                <div className="card-text">
                    <p data-testid="movie-card" className="fw-bold">{movie.original_title}</p>
                </div>
            </Link>
        </div>
     );
}

export default Card;
