import React from "react";
import { useEffect, useState } from "react";
import Card from "../components/Card";
import Loader from "./Loader";

function HomePage() {

    const [movie, setMovie] = useState([''])
    const [loading, setLoading] = useState(true)
    const options = {
        method: 'GET',
        headers: {
          accept: 'application/json',
          Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI2Y2RhYjEzOTcxZTA4NmU3Zjc2YTdmZDI4ZmU2YTFhZCIsInN1YiI6IjYzYjAxOWQxNTc1MzBlMDA4NTAxM2FmMiIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.JZkce-xL6W_73mKqWEjqa-lcWH8oXbJ_2bq56tw85bQ'
        }
    };
    useEffect(()=> {
        fetch('https://api.themoviedb.org/3/discover/movie?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc', options)
        .then(response => response.json())
        .then(data => {
            setMovie((data.results).slice(0, 10))
        })
        .catch(err => console.error(err));
        if(movie) {
            setLoading(false)
        }
    }, [loading])
  
    const topMovies = 
        movie.map((topMovie, i)=> {
            return (
                <div className="col-sm-6 col-lg-3 me-auto ms-auto" key={i}>
                    <Card movie={topMovie}/>
                </div>
            )
        })
    
    return ( 
        <div className="container">
            {loading && <Loader />}
            <div className="row g-5 justify-space-between">
                <div className="col-lg-6"><h4></h4>Top Rated Movie</div>
                {topMovies}
            </div>
        </div>
     );
}

export default HomePage;
