import React, { useState } from "react";
import imageLoad from "../assests/imageLoad.jpg"
import Loader from "../pages/Loader";
import { Link } from "react-router-dom";

function Card({movie, loading}) {

    const movieUrl = `/movie/${(movie.id)}`
    const imgSrc = `https://image.tmdb.org/t/p/original${movie.poster_path}`
    const [notification, setNotification ] = useState(true)
    const addToFavourite = () => {
        setNotification((prevNotification) => {
          const newNotification = !prevNotification;
          if (newNotification) {
            alert("Added to favorites");
          } else {
            alert("Removed from favorites");
          }
          return newNotification;
        });
    };
    return ( 
        <div className="card" data-testid="movie-card" >
            <Link to={movieUrl}>
                { loading ? <Loader /> 
                : movie.poster_path == null ? 
                <img data-testid="movie-poster" src={imageLoad} alt={movie.original_title} width={'220px'} /> 
                : <img data-testid="movie-poster" src={imgSrc} alt={movie.original_title} width={'100%'} /> 
            }
                <div className="card-text">
                    <p data-testid="movie-title" className="fw-bold  t-text">{movie.original_title}</p>
                    <span data-testid="movie-release-date" className="mfs-1">{movie.release_date}</span>
                </div>
            </Link>
            <button className="circle" onClick={addToFavourite}>
                {notification ? 
                    <i class="fa-regular fa-heart "></i>
                    : <i class="fa-solid fa-heart"></i>
                }
            </button>
        </div>
     );
}

export default Card;
