import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import Fuse from 'fuse.js';

function Navbar({setSearchText, background}) {
    const nav = useNavigate()
    const [search, setSearch] = useState('')

    const updateSearch = (e) => {
        setSearch(e.target.value)
        console.log(search)
    }

    const updateSearchText = (e) => {
        e.preventDefault()
        setSearchText(use.search(search))
        console.log(e.target.value)
        nav('/search')
    }
    return ( 
        <div className="nav" style={background}>
                <div className="logo">
                    <Link to="/">
                        <div>
                            LoGO
                        </div>
                        <span className="sd-none">MovieBox</span>
                    </Link>
                </div>
                <div className="search">
                    <form>
                        <input 
                        type="search" 
                        value={search} 
                        onChange={updateSearch} />
                        <button onClick={updateSearchText} className="m-btn"> 
                            <i className="fa-solid fa-magnifying-glass"></i>
                        </button>
                    </form>
                </div>
               <div className="sign">
                <Link className='sd-none' to="/">Sign in</Link>
                <button className="signBtn"><i className="fas fa-bars"></i></button>
               </div>
        </div>
     );
}

export default Navbar;
