        movie.map((obj, i)=> {
            return ( 
                <div className="col-lg-3" key={i}>
                    <Card movie={obj}/>
                </div>
            )
        }
    )

    return ( 
        <>
            {loading && <Loader />}
            <div className="container">
                <div className="row g-5 justify-space-between">
                    {searchResults}
                </div>
            </div>
        </>
     );
}

export default SearchedMoviePage;
