import React from "react";
import { useEffect, useState } from "react";
import Card from "../components/Card";
import Loader from "./Loader";
import { Link } from "react-router-dom";
import Modal from "../components/Modal";

function HomePage() {

    const [movie, setMovie] = useState([''])
    const [movieStatic, setMovieStatic] = useState([''])
    const [movieBackdrop, setMovieBackdrop] = useState([''])
    const [video, setVideo] = useState([''])
    const [loading, setLoading] = useState(true)
    const options = {
        method: 'GET',
        headers: {
          accept: 'application/json',
          Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI2Y2RhYjEzOTcxZTA4NmU3Zjc2YTdmZDI4ZmU2YTFhZCIsInN1YiI6IjYzYjAxOWQxNTc1MzBlMDA4NTAxM2FmMiIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.JZkce-xL6W_73mKqWEjqa-lcWH8oXbJ_2bq56tw85bQ'
        }
    };
    useEffect(()=> {
        fetch('https://api.themoviedb.org/3/discover/movie?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc', options)
        .then(response => response.json())
        .then(data => {
            setMovie((data.results).slice(0, 10))
            setMovieBackdrop((data.results).slice(10, 14))
            setMovieStatic((data.results[15]))
            console.log(data.results[15])
        })
        .catch(err => console.error(err));
        if(movie) {
            setLoading(false)
        }
    }, [loading])

    useEffect(()=> {
        const urlVideo = `https://api.themoviedb.org/3/movie/${movieStatic.id}?&append_to_response=videos&api_key=6cdab13971e086e7f76a7fd28fe6a1ad`
        fetch(urlVideo)
        .then(response => response.json())
        .then(data => {
            setLoading(false)
            setVideo(data?.videos?.results[15] || []);
            console.log(data?.videos?.results[15] || [])
        })
        
    }, [movieStatic.id])
    const videoKey = video.key
    // const backdropPath = `https://image.tmdb.org/t/p/original${movieStatic.backdrop_path}`
    const pipLink = document.getElementById('pip-link');
    const pipVideo = document.getElementById('pip-video');

    pipLink.addEventListener('click', (e) => {
    e.preventDefault(); // Prevent the link from navigating to a new page

    if (pipVideo) {
        if (pipVideo.requestPictureInPicture) {
        // Check if the browser supports PiP
        pipVideo.requestPictureInPicture(); // Request PiP mode
        } else {
        console.log('Picture-in-Picture is not supported in this browser.');
        // Provide a fallback for browsers that don't support PiP
        }
    }
    });
    const topMovies = 
        movie.map((topMovie, i)=> {
            return (
                <div className="col-sm-6 col-lg-3 me-auto ms-auto" key={i}>
                    <Card movie={topMovie}/>
                </div>
            )
        })
    const slide = 
        movieBackdrop.map((slide, i) => {
            return (
                <div className="carousel-item" data-bs-interval="6000" key={i}>
                    {loading ? <Loader /> : 
                    <>
                        <img 
                        src={`https://image.tmdb.org/t/p/original${slide.backdrop_path}`} className="overlayed-image" 
                        alt={slide.original_title} 
                        width={'100%'} />
                        <div className="overlay"></div>
                    </>
                    }
                    <div className="inner-text">
                        <h3>{slide.original_title}</h3>
                        <div className="holder">
                            <div className="vote"> TMDb </div>
                            <p className="mfs-2 d-inline-block">{slide.vote_count}</p>
                            <p className="mfs-2 d-inline-block al-end">{slide.popularity}</p>
                            <div className="popularity al-end"> PPL </div>
                        </div>
                        <p>{slide.overview}</p>
                        <Link to={'/'} className="link"> WATCH TRAILER </Link>
                    </div>
                </div>
            )
           
        })
    const Modal = () => {
        return ( 
            <div className="modal" id="videoModal">
                <div className="modal-content" id="pip-video">
                    <iframe
                        id="videoPlayer"
                        width="560"
                        height="315"
                        src=""
                        frameborder="0"
                        allowfullscreen
                    ></iframe>
                    <span className="close" id="closeModal">&times;</span>
                </div>
            </div>
            );
    }
    
    return ( 
        <>
        {Modal}
            <div className="container-fluid p-0">
                <div className="col-lg-12 col-sm-12 ">
                    <div id="carouselExampleIndicators" className="carousel slide" data-bs-ride="carousel" >
                        <div className="carousel-indicators">
                            <button
                                type="button"
                                data-bs-target="#carouselExampleIndicators"
                                data-bs-slide-to="0"
                                className="active"
                                aria-current="true"
                                aria-label="Slide 1"
                            ></button>
                            <button
                                type="button"
                                data-bs-target="#carouselExampleIndicators"
                                data-bs-slide-to="1"
                                aria-label="Slide 2"
                            ></button>
                            <button
                                type="button"
                                data-bs-target="#carouselExampleIndicators"
                                data-bs-slide-to="2"
                                aria-label="Slide 3"
                            ></button>
                            <button
                                type="button"
                                data-bs-target="#carouselExampleIndicators"
                                data-bs-slide-to="3"
                                aria-label="Slide 4"
                            ></button>
                            <button
                                type="button"
                                data-bs-target="#carouselExampleIndicators"
                                data-bs-slide-to="4"
                                aria-label="Slide 5"
                            ></button>
                        </div>
                        {/* CAROUSEL INNER */}
                        <div className="carousel-inner ">
                            <div className="carousel-item active" data-bs-interval="5000">
                                {loading ? <Loader /> :
                                <>
                                    <img 
                                    src={`https://image.tmdb.org/t/p/original${movieStatic.backdrop_path}`} 
                                    alt={movieStatic.original_title} 
                                    className="overlayed-image"
                                    width={'100%'} />
                                    <div className="overlay"></div>
                                </>
                                
                            }
                                <div className="inner-text">
                                    <h3>{movieStatic.original_title}</h3>
                                    <div className="holder">
                                        <div className="vote"> TMDb </div>
                                        <p className="mfs-2 d-inline-block">{movieStatic.vote_count}</p>
                                        <p className="mfs-2 d-inline-block al-end">{movieStatic.popularity}</p>
                                        <div className="popularity al-end"> PPL </div>
                                    </div>
                                    <p>{movieStatic.overview}</p>
                                    <Link 
                                    to={`https://www.youtube.com/embed/${videoKey}`} 
                                    id="pip-link"
                                    className="link"
                                    target="_blank"
                                    rel="noopener noreferrer"> 
                                        <i class="fas fa-play-circle"></i>
                                        WATCH TRAILER
                                    </Link>
                                </div>
                            </div>
                            {slide}
                        </div>
                        <button
                        className="carousel-control-prev"
                        type="button"
                        data-bs-target="#carouselExampleIndicators"
                        data-bs-slide="prev"
                        >
                        <span
                            className="carousel-control-prev-icon visually-hidden"
                            aria-hidden="true"
                        ></span>
                        <span className="visually-hidden">Previous</span>
                        </button>
                        <button
                        className="carousel-control-next"
                        type="button"
                        data-bs-target="#carouselExampleIndicators"
                        data-bs-slide="next"
                        >
                        <span
                            className="carousel-control-next-icon visually-hidden"
                            aria-hidden="true"
                        ></span>
                        <span className="visually-hidden">Next</span>
                        </button>
                    </div>
                </div>
            </div>
            <div className="container">
                {loading && <Loader />}
                <div className="row g-5 justify-space-between">
                    <div className="col-12 pd-0">
                        <div className="row pd-0">
                            <div className="col-lg-6 al-l"><h4 className="m-0 pd-0">Top Rated Movie</h4></div>
                            <div className="col-lg-6 al-r "><Link to="/">see more</Link></div>
                        </div>
                    </div>
                    {topMovies}
                </div>
            </div>
        </>
     );
}

export default HomePage;
